import React from "react";

const spinnerDimensions = {
  width: "250px",
  height: "450px",
  border: "1px solid teal",
};

const boxDimensions = {
  width: "250px",
  height: "50px",
  border: "1px solid teal",
};

export const Spinner = ({ staticNameArray, currentName }) => {
  return (
    <div style={spinnerDimensions} className="wrapper">
      {currentName}
      {staticNameArray.map((name, i) => (
        <div
          key={i}
          className="box absolute z-10  text-white text-xl flex items-center justify-center bg-red-500 "
          style={boxDimensions}
        >
          {name}
        </div>
      ))}
    </div>
  );
};
